import React, { useState, useEffect } from "react";
import { getGallery } from "../api";

export default function Gallery({ onItemSelect }) {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadGallery();
  }, []);

  const loadGallery = async () => {
    try {
      setLoading(true);
      const data = await getGallery();
      setItems(data.items || []);
    } catch (err) {
      setError("Failed to load gallery items");
      console.error("Gallery loading error:", err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="bg-white p-6 rounded-2xl shadow-md w-full max-w-6xl mx-auto">
        <h2 className="text-xl font-bold mb-4">Library Collection Gallery</h2>
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-2 text-gray-600">Loading library items...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-white p-6 rounded-2xl shadow-md w-full max-w-6xl mx-auto">
        <h2 className="text-xl font-bold mb-4">Library Collection Gallery</h2>
        <div className="text-center py-8">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={loadGallery}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white p-6 rounded-2xl shadow-md w-full max-w-4xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Library Collection</h2>
      <p className="text-gray-600 mb-6">Select an item from the library collection to generate a video script:</p>

      <div className="space-y-3">
        {items.map((item) => (
          <div
            key={item.id}
            className="border rounded-lg p-4 hover:shadow-md transition-shadow cursor-pointer bg-gray-50 hover:bg-gray-100"
            onClick={() => onItemSelect(item)}
          >
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <h3 className="font-semibold text-lg text-blue-800 mb-2">{item.title}</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-2 text-sm text-gray-600 mb-2">
                  <div><span className="font-medium">Creator:</span> {item.creator}</div>
                  <div><span className="font-medium">Date:</span> {item.date}</div>
                  <div><span className="font-medium">Call Number:</span> {item.call_number}</div>
                </div>
                <p className="text-sm text-gray-700">{item.description}</p>
              </div>
              <div className="ml-4">
                <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors text-sm font-medium">
                  Generate Script
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {items.length === 0 && (
        <div className="text-center py-8">
          <p className="text-gray-600">No library items available.</p>
        </div>
      )}
    </div>
  );
}
